// BlogDetails.js
import { useNavigate, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const BlogDetails = () => {
  const { id } = useParams();
  const { data: blog, error, isPending } = useFetch('http://localhost:3001/blogs/' + id);
  const Navigate = useNavigate();
  const handleClick = () => {
    fetch('http://localhost:3001/blogs/' + blog.id, {
      method: 'DELETE'
    }).then(() => {
      Navigate('/');
    });
  };

  return (
    <div className="container mt-5">
      { isPending && <div className="text-center">Loading...</div> }
      { error && <div className="alert alert-danger">{ error }</div> }
      { blog && (
        <div className="card">
          <div className="card-body">
            <h2 className="card-title">{ blog.title }</h2>
            <p className="card-text">Written by - { blog.author }</p>
            <p className="card-text">{ blog.body }</p>
            <button onClick={handleClick} className="btn btn-danger">Delete</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default BlogDetails;
