// Bloglist.js
import { Link } from "react-router-dom";

const Bloglist = ({ blogs, title }) => {
    return (
        <div className="blog-list">
            <h2 className="text-primary mb-4">{title}</h2>
            {blogs ? (
                blogs.map((blog) => (
                    <div className="blog-preview card mb-3" key={blog.id}>
                        <div className="card-body">
                            <h2 className="card-title">{blog.title}</h2>
                            <p className="card-text">Written by - {blog.author}</p>
                            <div className="row">
                                <div className="col">
                                    <Link to={`/blogs/${blog.id}`} className="btn btn-primary btn-block">Read More</Link>
                                </div>
                            </div>
                        </div>
                    </div>
                ))
            ) : (
                <div>No blogs to display</div>
            )}
        </div>
    );
}

export default Bloglist;
