import { Link } from "react-router-dom";
import { FaHome, FaPlus } from "react-icons/fa";

const Navbar = () => {
  const linkStyle = {
    fontSize: '1.2rem',
    padding: '10px',
    color: 'white',
    transition: 'background-color 0.3s ease',
    textDecoration: 'none'
  };

  const hoverStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.1)'
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <div className="container">
        <Link className="navbar-brand d-flex align-items-center" to="/">
          <span className="me-2" style={{ fontSize: '2rem' }}>üìù</span> Blog App
        </Link>

        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/" style={linkStyle} activeStyle={hoverStyle}>
                <FaHome className="me-1" /> Home
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/Create" style={linkStyle} activeStyle={hoverStyle}>
                <FaPlus className="me-1" /> New Blog
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
