// Bloglist.js
import { Link } from "react-router-dom";

const Bloglist = ({ blogs, title }) => {
    return (
        <div className="blog-list">
            <h2 className="text-primary mb-4">{title}</h2>
            <div className="row d-flex">
                {blogs ? (
                    blogs.map((blog) => (
                        <div className="col-md-6 mb-4 d-flex align-items-stretch" key={blog.id}>
                            <div className="card h-100">
                                <div className="card-body">
                                    <h1><b className="card-title">{blog.title}</b></h1>
                                    <h4 className="card-text">Written by - {blog.author}</h4>
                                </div>
                                <div className="card-footer">
                                    <Link to={`/blogs/${blog.id}`} className="btn btn-primary btn-block w-100">Read Blog</Link>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <div className="col">
                        <p>No blogs to display</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Bloglist;
