import { useEffect, useState } from "react";
import Bloglist from "./Bloglist";


const Home = () => {

    const [blogs, setBlogs] = useState(null)
    const [isWaiting, setIsWaiting] = useState(true)
    const [error, setError] = useState(null)

    useEffect(() => {
        
        setTimeout(() => {
            fetch('http://localhost:8000/blogs')
            .then(res => {
                return res.json();
            })
            .then(data => {
                // console.log(data);
                setBlogs(data);
                setIsWaiting(false);
            });
        }, 1000);   // setTimeout function is used to show user 'Loading...' msg (isWaiting). If you dont use setTimeut function user cant see 'Loading...' msg because isWaiting will change state very fast

    }, [])

    return (
        <div className="home">
            { isWaiting && <div>Loading...</div> }
            {blogs && <Bloglist blogs={blogs} title="All Blogs" />}
        </div>
    );
}

export default Home;

// npx json-server --watch data/db.json --port 8000
/*
    http://localhost:8000/blogs
    /blogs          GET     fetch all blogs
    /blogs/{id}     GET     fetch a single data
    /blogs          POST    add a new blog
    /blogs/{id}     DELETE  delete a blog
*/
