import { useNavigate, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const BlogDetails = () => {
  const { id } = useParams();
  const { data: blog, error, isPending } = useFetch('http://localhost:3001/blogs/' + id);
  const Navigate = useNavigate();

  const handleClick = () => {
    fetch('http://localhost:3001/blogs/' + blog.id, {
      method: 'DELETE'
    }).then(() => {
      Navigate('/');
    }) 
  }

  // return (
  //   <div className="blog-details">
  //     { isPending && <div>Loading...</div> }
  //     { error && <div>{ error }</div> }
  //     { blog && (
  //       <article>
  //         <h2>{ blog.title }</h2>
  //         <p>Written by - { blog.author }</p>
  //         <div>{ blog.body }</div>
  //         <button className="w-100" onClick={handleClick}>Delete</button>
  //       </article>
  //     )}
  //   </div>
  // );
  return (
    <div className="container mt-5">
      { isPending && <div className="text-center">Loading...</div> }
      { error && <div className="alert alert-danger">{ error }</div> }
      { blog && (
        <div className="card col-lg-10 mx-auto">
          <div className="card-body">
            <h2 className="card-title text-primary mb-4">{ blog.title }</h2>
            <p className="card-text">Written by - { blog.author }</p>
            <p className="card-text">{ blog.body }</p>
            <button onClick={handleClick} className="btn btn-danger btn-lg btn-block w-100 mt-3">Delete</button>
          </div>
        </div>
      )}
    </div>
  );
}
 
export default BlogDetails;
